break_block.cppExample:
for (int i = 0; i < 5; i++) {
    if (i) break;
    cout << i << " ";
cout << "6";
}
//output = 0 6

continue_block.cppExample:
for (int i = 0; i < 5; i++) {
    if (i) continue;
    cout << i << " ";
cout << "6";
}
//output = 0 2 3 4 6

