define_function.cppExample:
int add(int a, int b) {
    return a + b;
}

int main() {
    int a = 1, b = 2;
    cout << add(a, b);
    // output = 3
}

define_function_void.cppExample:
void add(int a, int b, int &num) {
    num = a + b;
    return ;
}

int main() {
    int a = 1, b = 2, num = 0;
    add(a, b, num);
    cout << num;
    // output = 3
}

